app.controller("HomeController",["$scope","$http","$location",function(t,o,e){t.movieSearch=function(){o.get("http://www.omdbapi.com/?s="+t.title).then(function(o){t.results=o.data.Search,e.path("/")},function(o){console.log(o),t.results=["Could not connect to the database, please try again"]})}}]),app.controller("ShowController",["$scope","$routeParams","$http","$location",function(t,o,e,n){t.test="asdf",e.get("http://www.omdbapi.com/?i="+o.movieId+"&tomatoes=true").then(function(o){t.info=o.data},function(t){console.log(t),n.path("/")})}]);